import React,{useState, useEffect} from 'react'

const Products = () => {

    const [data, setData] = useState([]);
    const [filter, setFilter] = useState(data);
    const [loading, setLoading] = useState(false);
    let compinentMounted = true;

    useEffect(() => {
        const getProducts = async () => {
            setLoading(true);
            const response = await fetch("https://tlv-events-app.herokuapp.com/events/uk/london");
            if(compinentMounted){
                setData(await response.clone().json());
                setFilter(await response.json());
                setLoading(false);
                console.log(filter);
            }
        }
        return () => {
            compinentMounted = false
        }

        getProducts()
    }, [])

    const Loading = ()=>{
        return(
            <>
                Loading....
            </>
        )
    }
    const ShowProducts = ()=>{
        return(
            <>
            <div className="buttons">
                <button className="btn btn-outline-dark">All</button>
                <button className="btn btn-outline-dark me-2">M</button>
                <button className="btn btn-outline-dark me-2">Mdwaa</button>
                <button className="btn btn-outline-dark me-2">Mf</button>
                <button className="btn btn-outline-dark me-2">Mfff</button>
            </div>

            {filter.map((product) => { 
                return(
                    <>
                        <div className="col-md-3">
                        <div class="card">
                        <img src={product.image} class="card-img-top" alt={product.title}/>
                        <div class="card-body">
                          <h5 class="card-title">{product.title}</h5>
                          <p class="card-text">{product.price}</p>
                          <a href="#" class="btn btn-primary">Go somewhere</a>
                        </div>
                        </div>
                        </div>
                    </>
                );
            })}
            </>
        );
    }

    return (
        <div>
            <div className="container my-5 py-5">
                <div className="row">
                    <div className="col-12 mb-5">
                        <h1 className="display-6 fw-bolder text-center">Latest Prodicts</h1>
                        <hr/>
                    </div>
                </div>
                <div className="row justify-content-center">
                    {loading ? <Loading/> : <ShowProducts/>}
                </div>
            </div>
        </div>
    )
}

export default Products
