import React, { useState, useEffect } from "react";

const Products = () => {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState(data);
  const [loading, setLoading] = useState(false);
  let componentMounted = true;

  useEffect(() => {
    const getProducts = async () => {
      setLoading(true);
      const response = await fetch(
        "https://tlv-events-app.herokuapp.com/events/uk/london"
      );
      if (componentMounted) {
        setData(await response.clone().json());
        setFilter(await response.json());
        setLoading(false);
        console.log(filter);
      }
      return () => {
        componentMounted = false;
      };
    };

    getProducts();
  }, []);

  const Loading = () => {
    return <>Loading....</>;
  };
  const ShowProducts = () => {
    return (
      <>
        {/* <div className="buttons">
          <button className="btn btn-outline-dark">All</button>
          <button className="btn btn-outline-dark me-2">M</button>
          <button className="btn btn-outline-dark me-2">Mdwaa</button>
          <button className="btn btn-outline-dark me-2">Mf</button>
          <button className="btn btn-outline-dark me-2">Mfff</button>
        </div> */}

        {filter.map((product) => {
          return (
            <>
              <div className="col-md-3 mb-4">
                <div class="card h-100 text-center "key={product.id}>
                  <h6 class="card-title m-2">{product.title.substring(0,32)}...</h6>
                  <img
                    src={product.flyerFront}
                    class="card-img-top"
                    alt={product.title}
                    height="100%"
                  />
                  <div class="card-body">
                    <p class="card-text">{product.date}</p>
                    <h6 class="card-title">
                      <a
                        href={product.venue.direction}
                        alt={product.venue.name}
                      >
                        {product.venue.name}
                      </a>
                    </h6>
                    <a href="#" class="btn btn-outline-dark">
                      +
                    </a>
                  </div>
                </div>
              </div>
            </>
          );
        })}
      </>
    );
  };

  return (
    <div>
      <div className="container my-5 py-5">
        <div className="row">
          <div className="col-12 mb-5">
            <h1 className="display-6 fw-bolder text-center">Public Events</h1>
            <hr />
          </div>
        </div>
        <div className="row justify-content-center">
          {loading ? <Loading /> : <ShowProducts />}
        </div>
      </div>
    </div>
  );
};

export default Products;
